if (*endptr == '-')
  {
    p = endptr + 1;
    if (*p == '\0' || *p == ',')
      {
        /* Open-ended: "500-" means 500 to end */
        *end = file_size - 1;
      }
    else
      {
        val = strtoll (p, &endptr, 10);
        if (endptr == p)
          return 0;
        *end = (off_t)val;
      }
